import logo from "../assets/img/logo.png";
import { Helmet } from "react-helmet";
import React, { useState, useEffect, useCallback } from "react";
import { useDispatch } from "react-redux";
import { axiosInstance } from "../helpers/axiosInstance";
import { HideLoading, ShowLoading } from "../redux/alertsSlice";
import Game from "../components/Game";
import { Row, Col, message } from "antd";
import { Link } from "react-router-dom";

function Index() {
  const dispatch = useDispatch();
  const [games, setGames] = useState([]);
  const [cities, setCities] = useState([]);
  const [filters, setFilters] = useState({});

  const getGamesByFilter = useCallback(async () => {
    dispatch(ShowLoading());
    const departure = filters.departure;
    const arrival = filters.arrival;
    const staringDate = filters.staringDate;
    try {
      const { data } = await axiosInstance.post(
        `/api/games/get?departure=${departure}&to=${arrival}&staringDate=${staringDate}`
      );

      setGames(data.data);
      dispatch(HideLoading());
    } catch (error) {
      dispatch(HideLoading());
      message.error(error.response.data.message);
    }
  }, [filters, dispatch]);

  useEffect(() => {
    axiosInstance.get("/api/cities/get-all-cities").then((response) => {
      setCities(response.data.data);
    });
  }, []);

  useCallback(() => {
    if (filters.departure && filters.arrival && filters.staringDate) {
      getGamesByFilter();
    }
  }, [filters.departure, filters.arrival, filters.staringDate, getGamesByFilter]);

  return (
    <>
      <Helmet>
        <title>Easy-Booking</title>
      </Helmet>
      <div className="h-screen flex bg-gray-900">
        <div
          className="hero min-h-screen lg:flex w-full lg:w-3/4"
          style={{
            backgroundImage: `url("https://cdn.dribbble.com/users/1976094/screenshots/4687414/buss_trvl.gif")`,
            backgroundSize: "cover",
            backgroundPosition: "center center",
          }}
        >
          <div className="flex items-center h-full w-full">
            <div className="h-screen overflow-auto overflow-x-hidden">
              <div className="bg-opacity-80">
                <Row gutter={[15, 15]}>
                  {games.map((game, index) => {
                    return (
                      <div key={index} className="w-screen p-10 ">
                        <Game game={game} />
                      </div>
                    );
                  })}
                </Row>
              </div>
            </div>
          </div>
        </div>

        <div className="hero-content text-center text-neutral-content">
          <div className="max-w-md ">
            <div className="flex justify-center">
              <img
                className=" text-center w-20 h-20 rounded-full"
                src={logo}
                alt="logo"
              />
            </div>

            <h1 className="mb-5 text-5xl text-white font-bold ">
              Easy-Booking
            </h1>
            <p className="mb-5 text-xl text-white">
              is a platform that allows you to book your bus tickets online and
              in a very easy way.
            </p>
            <Link
              to="/login"
              className="relative inline-flex items-center justify-start
                px-10 py-3 overflow-hidden font-bold rounded-full
                group"
            >
              <span className="w-32 h-32 rotate-45 translate-x-12 -translate-y-2 absolute left-0 top-0 bg-white opacity-[3%]"></span>
              <span className="absolute top-0 left-0 w-48 h-48 -mt-1 transition-all duration-500 ease-in-out rotate-45 -translate-x-56 -translate-y-24 bg-blue-600 opacity-100 group-hover:translate-x-1"></span>
              <span className="relative w-full text-left text-white transition-colors duration-200 ease-in-out group-hover:text-white">
                Check your tickets
              </span>
              <span className="absolute inset-0 border-2 border-blue-600 rounded-full"></span>
            </Link>
            <div className="w-full my-5 mx-2 p-2 px-2 py-3 flex justify-center">
              <Row gutter={10} align="center">
                <Col lg={12} sm={24}>
                  <select
                    className="mb-5 select select-primary w-full max-w-xs"
                    onChange={(e) => {
                      setFilters({ ...filters, departure: e.target.value });
                    }}
                  >
                    <option value="">departure</option>
                    {cities.map((data, index) => {
                      return (
                        <option key={index} value={data.ville}>
                          {data.ville}
                        </option>
                      );
                    })}
                  </select>
                </Col>
                <Col lg={12} sm={24}>
                  <select
                    className="mb-5 select select-primary w-full max-w-xs"
                    onChange={(e) => {
                      setFilters({ ...filters, arrival: e.target.value });
                    }}
                  >
                    <option value="">arrival</option>
                    {cities.map((data, index) => {
                      return (
                        <option key={index} value={data.ville}>
                          {data.ville}
                        </option>
                      );
                    })}
                  </select>
                </Col>
                <Col lg={24} sm={24}>
                  <input
                    className="mb-5 input input-bordered input-primary w-full max-w-xs"
                    min={new Date().toISOString().split("T")[0]}
                    type="date"
                    placeholder="Date"
                    onChange={(e) => {
                      setFilters({
                        ...filters,
                        staringDate: e.target.value,
                      });
                    }}
                  />
                </Col>
                <Col lg={24} sm={24}>
                  <div className="flex justify-center gap-4">
                    <button
                      onClick={() => {
                        getGamesByFilter();
                      }}
                      className="relative inline-flex items-center justify-start
                    px-10 py-3 overflow-hidden font-bold rounded-full
                    group"
                    >
                      <span className="w-32 h-32 rotate-45 translate-x-12 -translate-y-2 absolute left-0 top-0 bg-white opacity-[3%]"></span>
                      <span className="absolute top-0 left-0 w-48 h-48 -mt-1 transition-all duration-500 ease-in-out rotate-45 -translate-x-56 -translate-y-24 bg-blue-600 opacity-100 group-hover:-translate-x-8"></span>
                      <span className="relative w-full text-left text-white transition-colors duration-200 ease-in-out group-hover:text-white">
                        Search
                      </span>
                      <span className="gap-5 absolute inset-0 border-2 border-blue-600 rounded-full"></span>
                    </button>
                  </div>
                </Col>
                <div className="flex justify-center gap-4 mt-5 w-full">
                  {games.length === 0 && (
                    <div className="text-center text-white text-2xl">
                      Make your search now
                    </div>
                  )}
                </div>
              </Row>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Index;
